#' Adjusted Mazziotta-Pareto Index
#'
#' 
#' @param data .
#' @param indicators .
#' @param stat_unit .
#' @param time_column_long .
#' @param polarity .
#' @param ref .
#' @param penalty .
#' @param saving_path .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$getting_started} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$data_scores} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$CI_table} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$CI_table$asDF}
#'
#' \code{as.data.frame(results$CI_table)}
#'
#' @export
ampi <- function(
    data,
    indicators,
    stat_unit,
    time_column_long,
    polarity,
    ref = "list(ind1 = ref1, ind2=ref2, ...)",
    penalty = "POS",
    saving_path) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("ampi requires jmvcore to be installed (restart may be required)")

    if ( ! missing(indicators)) indicators <- jmvcore::resolveQuo(jmvcore::enquo(indicators))
    if ( ! missing(stat_unit)) stat_unit <- jmvcore::resolveQuo(jmvcore::enquo(stat_unit))
    if ( ! missing(time_column_long)) time_column_long <- jmvcore::resolveQuo(jmvcore::enquo(time_column_long))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(indicators), indicators, NULL),
            `if`( ! missing(stat_unit), stat_unit, NULL),
            `if`( ! missing(time_column_long), time_column_long, NULL))


    options <- ampiOptions$new(
        indicators = indicators,
        stat_unit = stat_unit,
        time_column_long = time_column_long,
        polarity = polarity,
        ref = ref,
        penalty = penalty,
        saving_path = saving_path)

    analysis <- ampiClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

